import React, { Component } from 'react';
import { spawn } from 'child_process';
// import {
//     BrowserRouter as Router,
//     Route,
//     Link,
//     Redirect,
//     Switch
//   } from 'react-router-dom';

// const logindata = [ { "user": "anupama" , "password":"abc" },
//                         { "user": "huma" , "password":"xyz" }
//                   ]

class Login extends Component {

    constructor(props){
        super(props);
        this.state = { 
            auth: {
                username:'ttn',
                password: 'ttn'
            },
            error: false
          }
    }



    handleLogin = (e) => {
        const { username, password, error} =this.state;
        const {name, value} = e.target;
        // if(name=={username} && value=={password}){
        //     alert("Correct Credentials");
        // }
        this.setState({ [name]: value});

    }

    handleSubmit = (e) =>{
    e.preventDefault();
    const { username, password, error , auth} = this.state;
    if(username === auth.username && password === auth.password){
        this.setState({
            error: false
        }, () => console.log(this.state))
    }
    else{
        this.setState({
            username: '',
            password: '',
            error: true
        })
    }
    }

    render() {
        const { username , password, error} =this.state;
        return (
        <div>
            <form>
                <label>Username:</label>

                <input  type="text" 
                        placeholder="enter username"  
                        onChange={this.handleLogin} required
                        name="username"
                        // value ={username}
                    />


                <label>Password:</label>

                <input  type="password" 
                        placeholder="enter password"  
                        onChange={this.handleLogin} required
                        name="password"
                        // value={password}
                />

                <input type="button" onClick={this.handleSubmit} value='Submit'/>
            </form>
            {
                (!this.state.error)? ( <span>no error</span> ) : ( <span>error</span> )
            }
        </div>
        );
}
}

export default Login;
