import React, { Component } from 'react';
// import BookDetails from './components/BookDetails';
// import {
//   BrowserRouter as Router,
//   Route,
//   // Link,
//   // Redirect,
//   // Switch
// } from 'react-router-dom';

class BookList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [
        {
          "id": "001",
          "name": "Silas Marner",
          "author": "George Eliot"
        },
        {
          "id": "002",
          "name": "Invisible Man",
          "author": "H.G. Wells"
        },
        {
          "id": "003",
          "name": "Pride and Prejudice",
          "author": "Jane Austen"
        }
      ]
    }
  }
  render() {
    const { books } = this.state;
    return (
      <div>
        <p>Book List</p>
        <ul>
          {books.map(booklist =>
            <li key={booklist.id} onClick={() => console.log(booklist.name)}>
            {booklist.name} by {booklist.author}
            </li>)}
        </ul>
        <BookDetails books = { books } /> }
{/* 
        <ul>

          {books.map(booklist =>
            <li key={booklist.id} onClick={() => 
              <Route
              path='/bookdetails'
              render={(props) => <BookDetails {...props} />}
            />
            }>
            </li>)}

        </ul> */}
      </div>
    );
  }
}

export default BookList;
