import React, { Component } from 'react';
import './App.css';
import Login from './components/Login';
import BookList from './components/BookList';
// import BookDetails from './components/BookDetails';

import {
  BrowserRouter as Router,
  Route,
  // Link,
  // Redirect,
  // Switch
} from 'react-router-dom';

class App extends Component {

  state = {
    isAuth: false
  }

  login = () =>  {
    this.setState ({
      isAuth: true
    })
  }

  render() {
    return (
      <div className = "App">
      <Router>



              <Route
                path = "/login"
    render = { <Login isAuth = { this.state.isAuth } loginCallback = { this.login } /> }/>

              <PrivateRoute
                path = "/booklist"
                component = { BookList }/>  

              {/* <PrivateRoute>
                path = "/bookdetails"
                component = { BookDetails }
              </PrivateRoute> */}

            </Router>

      </div>
    );
  }
}

export default App;

const PrivateRoute = ({component:Component, ...props}) => (
  <Route 
      {...props} 
      render =  { () => 
                  (this.state.isAuth? (<Component {...props}/> ) : 
                  <div> You have no access to this page </div>
                  // <Redirect to='/login'/>
                  ) 
      }
  />
)
