import React, { Component } from 'react';
import './App.css';
import Login from './components/Login';
import BookList from './components/BookList';
import BookDetails from './components/BookDetails';

import {
  BrowserRouter as Router,
  Route,
  // Link,
  Redirect,
  Switch
} from 'react-router-dom';

class App extends Component {

  state = {
    isAuth: false
  }

  login() {
    this.setState ({
      isAuth: true
    })
  }

  render() {
    return (
      <div className = "App">
        <Switch>
            <Router>

              <Route>
                path = "/login"
                component = { <Login isAuth = { this.state.isAuth } loginCallback = { this.login } /> }                
              </Route>

              <PrivateRoute>
                path = "/booklist"
                component = { BookList }
              </PrivateRoute>

              <PrivateRoute>
                path = "/bookdetails"
                component = { BookDetails }
              </PrivateRoute>

            </Router>
        </Switch>
      </div>
    );
  }
}

export default App;

const isAuth = !true;
const PrivateRoute = ({component:Component, ...props}) => (
  <Route 
      {...props} 
      render =  { () => 
                  (isAuth? (<Component {...props}/> ) : 
                  // <div> You have no access to this page </div>
                  <Redirect to='/login'/>) 
                }
  />
)
